mapper.select:
select * from chat_table A


and chat_room_id = #{chatRoomId} and (chat_date between #{startDate}
and #{endDate})

mapper.update:
update chat_room_table set
chat_room_title = NVL(#{chatRoomTitle},chat_room_title), chat_room_comment = NVL(#{chatRoomComment},chat_room_comment), chat_room_goal = NVL(#{chatRoomGoal},chat_room_goal), chat_room_max = NVL(#{chatRoomMax},chat_room_max), chat_room_category = NVL(#{chatRoomCategory},chat_room_category), chat_room_img = NVL(#{chatRoomImg},chat_room_img), chat_room_state = NVL(#{chatRoomState},chat_room_state), exit_opt = NVL(#{exitOpt},exit_opt), exit_ck = NVL(#{exitCk},exit_ck)
where chat_room_id=#{chatRoomId}

update chat_user_table set
room_auth = NVL(#{roomAuth},room_auth), chat_user_stamp = NVL(#{chatUserStamp},chat_user_stamp)
where chat_room_id=#{chatRoomId} and user_id = #{userId}

update chat_table set

	chat_state = NVL(#{chatUserStamp},chat_state),

	where chat_room_id=#{chatRoomId} and user_id = #{userId}

update chat_table set
chat_ck_cnt = ((select count(*) from chat_user_table where chat_room_id =#{chatRoomId}) - (select count(*) from chat_user_table where chat_room_id =#{chatRoomId} and chat_user_stamp > chat_date) )
where chat_room_id=#{chatRoomId}
